 */

plugins {
    // Apply the java-library plugin for API and implementation separation.
    `java-library`
    `maven-publish`
    signing
}

group = "org.zouarioss"
version = "1.0.0"

repositories {
    // Use Maven Central for resolving dependencies.
    mavenCentral()
}

dependencies {
    // Use JUnit Jupiter for testing.
    testImplementation(libs.junit.jupiter)

    testRuntimeOnly("org.junit.platform:junit-platform-launcher")

    // MariaDB JDBC driver for testing
    testImplementation("org.mariadb.jdbc:mariadb-java-client:3.3.2")

    // This dependency is exported to consumers, that is to say found on their compile classpath.
    api(libs.commons.math3)

    // This dependency is used internally, and not exposed to consumers on their own compile classpath.
    implementation(libs.guava)
}

// Apply a specific Java toolchain to ease working on different environments.
java {
    toolchain {
        languageVersion = JavaLanguageVersion.of(25)
    }
    withJavadocJar()
    withSourcesJar()
}

tasks.named<Test>("test") {
    // Use JUnit Platform for unit tests.
    useJUnitPlatform {
        // Exclude integration tests by default
        excludeTags("integration")
    }
}

// Add task for integration tests
tasks.register<Test>("integrationTest") {
    description = "Runs integration tests"
    group = "verification"

    testClassesDirs = sourceSets["test"].output.classesDirs
    classpath = sourceSets["test"].runtimeClasspath
 
    useJUnitPlatform {
        includeTags("integration")
    }

    shouldRunAfter(tasks.test)
}

publishing {
    publications {
        create<MavenPublication>("mavenJava") {
            from(components["java"])

            groupId = "org.zouarioss"
            artifactId = "skinned-rat-orm"
            version = "1.0.0"
 
           pom {
                name.set("Skinned Rat ORM")
                description.set("A lightweight ORM framework for Java with annotation-based entity mapping")
                url.set("https://github.com/zouari-oss/skinned-rat-orm")

                licenses {
                    license {
                        name.set("GPL-3.0 License")
                        url.set("https://opensource.org/license/GPL-3.0")
                    }
                }

                developers {
                    developer {
                        id.set("zouariomar")
                        name.set("ZouariOmar")
                        email.set("zouariomar20@gmail.com")
                    }
                }
 
                scm {
                    connection.set("scm:git:git://github.com/zouari-oss/skinned-rat-orm.git")
                    developerConnection.set("scm:git:ssh://github.com/zouari-oss/skinned-rat-orm.git")
                    url.set("https://github.com/zouari-oss/skinned-rat-orm")
                }
            }
        }
    }

    repositories {
        // Local Maven repository (for testing)
        maven {
            name = "local"
            url = uri("file://${buildDir}/repo")
        }

        // GitHub Packages
        maven {
            name = "GitHubPackages"
            url = uri("https://maven.pkg.github.com/zouari-oss/skinned-rat-orm")
            credentials {
                username = System.getenv("GITHUB_ACTOR")
                password = System.getenv("GITHUB_TOKEN")
            }
        }
    }
}

signing {
    // Only sign if publishing to Maven Central
    setRequired { gradle.taskGraph.hasTask("publishMavenJavaPublicationToOSSRHRepository") }
    sign(publishing.publications["mavenJava"])
}
